import { useParams } from "react-router-dom";
import projects from "../data/projects";

function ProjectDetail() {
  const { slug } = useParams();
  const project = projects.find((p) => p.slug === slug);

  if (!project) return <div className="text-center text-white">Project not found</div>;

  return (
    <div className="bg-[#0c0c0c] text-white px-6 py-20">
      <div className="max-w-6xl mx-auto space-y-12">
        {/* Header */}
        <div className="text-center">
          <h1 className="text-4xl font-bold mb-2">{project.title}</h1>
          <p className="text-sm text-gray-400">{project.type} — {project.date}</p>
        </div>

        {/* Grid Layout */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-12">
          {/* Column 1 — Info + Content */}
          <div className="md:col-span-2 space-y-10">
            <div className="space-y-3 text-sm text-gray-400 border-b border-gray-700 pb-6">
              <p><span className="font-semibold text-white">Role:</span> {project.role}</p>
              <p><span className="font-semibold text-white">Tools:</span> {project.tools.join(", ")}</p>
            </div>

            <div className="space-y-10 text-base leading-relaxed text-gray-300">
              <section>
                <h2 className="text-lg font-semibold text-white mb-2">Problem</h2>
                <p>{project.problem}</p>
              </section>
              <section>
                <h2 className="text-lg font-semibold text-white mb-2">Process</h2>
                <p>{project.process}</p>
              </section>
              <section>
                <h2 className="text-lg font-semibold text-white mb-2">Outcome</h2>
                <p>{project.outcome}</p>
              </section>
            </div>
          </div>

          {/* Column 2 — Screenshot Gallery */}
          <div className="space-y-6">
            {project.images.map((img, i) => (
              <img
                key={i}
                src={img}
                alt={`${project.title} screenshot ${i + 1}`}
                className="w-full rounded-lg border border-gray-800"
              />
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default ProjectDetail;
