import { useParams } from "react-router-dom";
import projects from "../data/projects";
import { Download, FileText, ExternalLink } from "lucide-react";

function ProjectDetail() {
  const { slug } = useParams();
  const project = projects.find((p) => p.slug === slug);

  if (!project) {
    return (
      <div className="text-center text-white py-20 text-lg">
        Project not found 😢
      </div>
    );
  }

  return (
    <div className="bg-[#0c0c0c] text-white px-6 py-16">
      <div className="max-w-6xl mx-auto space-y-12">
        {/* Back to Portfolio */}
        <div>
          <a
            href="/portfolio"
            className="inline-flex items-center gap-2 px-4 py-2 text-sm font-medium text-gray-200 bg-[#1a1a1a] rounded-full border border-gray-700 hover:bg-gray-800 hover:text-white transition duration-300"
          >
            ← Back to Portfolio
          </a>
        </div>

        {/* Title + Meta */}
        <div className="text-center space-y-2">
          <h1 className="text-5xl font-extrabold bg-gradient-to-r from-pink-400 via-purple-400 to-blue-400 text-transparent bg-clip-text">
            {project.title}
          </h1>
          <p className="text-gray-400 text-base">{project.description}</p>
        </div>

        {/* Featured Hero Image */}
        {project.screens.length > 0 && (
          <div className="max-w-4xl mx-auto">
            <img
              src={project.screens[0]}
              alt={`${project.title} main screen`}
              className="w-full h-auto rounded-xl border border-gray-800 shadow-lg"
            />
          </div>
        )}

        {/* Project Info & Screens */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-12 mt-10">
          {/* Left Column - Details */}
          <div className="md:col-span-2 space-y-10">
            {/* Role & Tools */}
            <div className="space-y-3 text-sm text-gray-400 border-b border-gray-700 pb-6">
              <p>
                <span className="font-semibold text-white">Role:</span>{" "}
                {project.role}
              </p>
              <p>
                <span className="font-semibold text-white">Tools:</span>{" "}
                {project.tools}
              </p>
            </div>

            {/* Project Content */}
            <div className="space-y-10 text-base leading-relaxed text-gray-300">
              {/* Overview */}
              <section>
                <h2 className="text-lg font-semibold text-transparent bg-gradient-to-r from-pink-400 via-purple-400 to-blue-400 bg-clip-text mb-2">
                  Overview
                </h2>
                <p>{project.overview}</p>
              </section>

              {/* Problem */}
              <section>
                <h2 className="text-lg font-semibold text-transparent bg-gradient-to-r from-pink-400 via-purple-400 to-blue-400 bg-clip-text mb-2">
                  Problem
                </h2>
                <ul className="list-disc list-inside space-y-2">
                  {project.problem.split(". ").map((point, idx) => (
                    <li key={idx}>{point}</li>
                  ))}
                </ul>
              </section>

              {/* Solution */}
              <section>
                <h2 className="text-lg font-semibold text-transparent bg-gradient-to-r from-pink-400 via-purple-400 to-blue-400 bg-clip-text mb-2">
                  Solution
                </h2>
                <ul className="list-disc list-inside space-y-2">
                  {project.solution.split(". ").map((point, idx) => (
                    <li key={idx}>{point}</li>
                  ))}
                </ul>
              </section>

              {/* Process */}
              <section>
                <h2 className="text-lg font-semibold text-transparent bg-gradient-to-r from-pink-400 via-purple-400 to-blue-400 bg-clip-text mb-2">
                  Process
                </h2>
                <ul className="list-disc list-inside space-y-2">
                  {project.process.split(". ").map((point, idx) => (
                    <li key={idx}>{point}</li>
                  ))}
                </ul>
              </section>

              {/* Outcome */}
              <section>
                <h2 className="text-lg font-semibold text-transparent bg-gradient-to-r from-pink-400 via-purple-400 to-blue-400 bg-clip-text mb-2">
                  Outcome
                </h2>
                <ul className="list-disc list-inside space-y-2">
                  {project.outcome.split(". ").map((point, idx) => (
                    <li key={idx}>{point}</li>
                  ))}
                </ul>
              </section>

              {/* Reflection */}
              <section>
                <h2 className="text-lg font-semibold text-transparent bg-gradient-to-r from-pink-400 via-purple-400 to-blue-400 bg-clip-text mb-2">
                  Reflection
                </h2>
                <p>{project.reflection}</p>
              </section>
            </div>

            {/* Buttons for Downloads / Links */}
            {(project.downloadLink ||
              project.pdfLink ||
              project.externalLink) && (
              <div className="flex flex-wrap gap-4 mt-8">

                {/* Download  */}
                {project.downloadLink && (
                  <a
                    href={project.downloadLink}
                    download
                    className="flex items-center gap-2 px-5 py-3 rounded-xl text-white font-medium bg-gradient-to-r from-pink-400 via-purple-500 to-blue-400 shadow-lg hover:shadow-xl transition-all duration-300 hover:scale-105"
                  >
                    <Download size={18} />
                    Download Demo/ Prototype
                  </a>
                )}

                {/* View Design Document */}
                {project.pdfLink && (
                  <a
                    href={project.pdfLink}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="flex items-center gap-2 px-5 py-3 rounded-xl text-white font-medium bg-gradient-to-r from-blue-400 via-purple-500 to-pink-400 shadow-lg hover:shadow-xl transition-all duration-300 hover:scale-105"
                  >
                    <FileText size={18} />
                    View Design Document
                  </a>
                )}

                {/* External Link */}
                {project.externalLink && (
                  <a
                    href={project.externalLink}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="flex items-center gap-2 px-5 py-3 rounded-xl text-white font-medium bg-gradient-to-r from-purple-500 via-pink-400 to-blue-400 shadow-lg hover:shadow-xl transition-all duration-300 hover:scale-105"
                  >
                    <ExternalLink size={18} />
                    Visit Project Repository
                  </a>
                )}
              </div>
            )}
          </div>

          {/* Right Column - Image Gallery */}
          <div className="space-y-6">
            {project.screens.slice(1).map((img, i) => (
              <img
                key={i}
                src={img}
                alt={`${project.title} screenshot ${i + 2}`}
                className="w-full rounded-lg border border-gray-800 shadow-lg hover:scale-[1.02] transition-transform duration-300"
              />
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default ProjectDetail;
