import { useParams } from "react-router-dom";
import projects from "../data/projects";

function ProjectDetail() {
  const { slug } = useParams();
  const project = projects.find((p) => p.slug === slug);

  if (!project)
    return <div className="text-center text-white py-20">Project not found</div>;

  return (
    <div className="bg-[#0c0c0c] text-white px-6 py-16">
      <div className="max-w-5xl mx-auto space-y-16">
        {/* Back to Portfolio */}
        <div>
          <a
            href="/portfolio"
            className="inline-flex items-center gap-2 px-4 py-2 text-sm font-medium text-gray-200 bg-[#1a1a1a] rounded-full border border-gray-700 hover:bg-gray-800 hover:text-white transition duration-200"
          >
            ‚Üê Back to Portfolio
          </a>
        </div>

        {/* Title & Description */}
        <div className="text-center space-y-4">
          <h1 className="text-4xl md:text-5xl font-bold">{project.title}</h1>
          <p className="text-gray-400 max-w-2xl mx-auto text-lg leading-relaxed">
            {project.description}
          </p>
        </div>

        {/* Hero Image */}
        {project.screens.length > 0 && (
          <div className="max-w-4xl mx-auto">
            <img
              src={project.screens[0]}
              alt={`${project.title} main screen`}
              className="w-full h-auto rounded-2xl border border-gray-800 shadow-lg"
            />
          </div>
        )}

        {/* Role & Tools */}
        <div className="flex flex-wrap gap-6 justify-center text-gray-400 text-sm mt-2">
          <p>
            <span className="font-semibold text-white">Role:</span> {project.role}
          </p>
          <p>
            <span className="font-semibold text-white">Tools:</span> {project.tools}
          </p>
        </div>

        {/* Main Content */}
        <div className="space-y-20">
          {/* Overview */}
          <section>
            <h2 className="text-2xl font-semibold mb-3">Overview</h2>
            <p className="text-gray-300 leading-relaxed">{project.overview}</p>
          </section>

          {/* Problem + Image Left */}
          <section className="grid md:grid-cols-2 gap-10 items-center">
            <div>
              <h2 className="text-2xl font-semibold mb-3">Problem</h2>
              <ul className="list-disc list-inside text-gray-400 space-y-2 leading-relaxed">
                {project.problem
                  .split(". ")
                  .filter((p) => p.trim())
                  .map((point, index) => (
                    <li key={index}>{point.trim()}</li>
                  ))}
              </ul>
            </div>
            {project.screens[1] && (
              <img
                src={project.screens[1]}
                alt="Problem visual"
                className="rounded-xl border border-gray-800 shadow-md"
              />
            )}
          </section>

          {/* Solution + Image Right */}
          <section className="grid md:grid-cols-2 gap-10 items-center">
            {project.screens[2] && (
              <img
                src={project.screens[2]}
                alt="Solution visual"
                className="rounded-xl border border-gray-800 shadow-md order-last md:order-first"
              />
            )}
            <div>
              <h2 className="text-2xl font-semibold mb-3">Solution</h2>
              <ul className="list-disc list-inside text-gray-400 space-y-2 leading-relaxed">
                {project.solution
                  .split(". ")
                  .filter((p) => p.trim())
                  .map((point, index) => (
                    <li key={index}>{point.trim()}</li>
                  ))}
              </ul>
            </div>
          </section>

          {/* Process */}
          <section>
            <h2 className="text-2xl font-semibold mb-3">Process</h2>
            <ul className="list-disc list-inside text-gray-400 space-y-2 leading-relaxed">
              {project.process
                .split(". ")
                .filter((p) => p.trim())
                .map((point, index) => (
                  <li key={index}>{point.trim()}</li>
                ))}
            </ul>
          </section>

          {/* Outcome */}
          <section>
            <h2 className="text-2xl font-semibold mb-3">Outcome</h2>
            <ul className="list-disc list-inside text-gray-400 space-y-2 leading-relaxed">
              {project.outcome
                .split(". ")
                .filter((p) => p.trim())
                .map((point, index) => (
                  <li key={index}>{point.trim()}</li>
                ))}
            </ul>
          </section>

          {/* Reflection */}
          <section>
            <h2 className="text-2xl font-semibold mb-3">Reflection</h2>
            <p className="text-gray-300 leading-relaxed">{project.reflection}</p>
          </section>
        </div>

        {/* Gallery */}
        {project.screens.length > 3 && (
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6 mt-10">
            {project.screens.slice(3).map((img, i) => (
              <img
                key={i}
                src={img}
                alt={`${project.title} screenshot ${i + 4}`}
                className="w-full rounded-xl border border-gray-800 shadow-lg hover:scale-105 transition-transform duration-300"
              />
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default ProjectDetail;
