import { useRef, useState } from "react";
import emailjs from "@emailjs/browser";

function Contact() {
  const form = useRef();
  const [status, setStatus] = useState("");

  const sendEmail = (e) => {
    e.preventDefault();
    setStatus("Sending...");

    emailjs
      .sendForm(
        "your_service_id",   // e.g. "service_123abc"
        "your_template_id",  // e.g. "template_xyz456"
        form.current,
        "your_public_key"    // e.g. "aBcDeFgHiJkLmNoP"
      )
      .then(
        (result) => {
          setStatus("Message sent successfully!");
          form.current.reset();
        },
        (error) => {
          setStatus("Failed to send message. Please try again.");
        }
      );
  };

  return (
    <section className="max-w-2xl mx-auto px-6 py-24 text-white">
      <h2 className="text-4xl font-bold mb-6 tracking-wide">Contact</h2>
      <p className="text-lg mb-6 text-gray-300">
        Let’s work together — I’d love to hear from you.
      </p>

      <form ref={form} onSubmit={sendEmail} className="space-y-5">
        <input
          type="text"
          name="user_name"
          placeholder="Your Name"
          required
          className="w-full bg-black border border-gray-600 px-4 py-3 rounded focus:outline-none focus:ring-2 focus:ring-white"
        />
        <input
          type="email"
          name="user_email"
          placeholder="Email"
          required
          className="w-full bg-black border border-gray-600 px-4 py-3 rounded focus:outline-none focus:ring-2 focus:ring-white"
        />
        <textarea
          name="message"
          placeholder="Message"
          required
          className="w-full bg-black border border-gray-600 px-4 py-3 rounded h-40 resize-none focus:outline-none focus:ring-2 focus:ring-white"
        />
        <button
          type="submit"
          className="bg-white text-black px-6 py-3 rounded font-semibold hover:bg-gray-200 transition"
        >
          Send
        </button>
        {status && <p className="text-sm text-green-400 mt-2">{status}</p>}
      </form>
    </section>
  );
}

export default Contact;
