import { useParams } from "react-router-dom";
import projects from "../data/projects";

function ProjectDetail() {
  const { slug } = useParams();
  const project = projects.find((p) => p.slug === slug);

  if (!project)
    return <div className="text-center text-white py-20">Project not found</div>;

  return (
    <div className="bg-[#0c0c0c] text-white px-6 py-16">
      <div className="max-w-5xl mx-auto space-y-16">
        {/* Back Button */}
        <div>
          <a
            href="/portfolio"
            className="inline-flex items-center gap-2 px-4 py-2 text-sm font-medium text-gray-300 bg-[#1a1a1a] rounded-full border border-gray-800 hover:bg-gray-800 hover:text-white transition duration-200"
          >
            ‚Üê Back to Portfolio
          </a>
        </div>

        {/* Title & Description */}
        <div className="text-center space-y-4">
          <h1 className="text-5xl font-extrabold tracking-tight text-white">
            {project.title}
          </h1>
          <p className="text-gray-400 max-w-2xl mx-auto">
            {project.description}
          </p>
        </div>

        {/* Hero Image */}
        {project.screens.length > 0 && (
          <div className="max-w-3xl mx-auto">
            <img
              src={project.screens[0]}
              alt={`${project.title} main`}
              className="w-full rounded-xl border border-gray-800 shadow-xl"
            />
          </div>
        )}

        {/* Role & Tools */}
        <div className="flex flex-col md:flex-row justify-center md:justify-between items-start md:items-center gap-6 border-b border-gray-800 pb-6">
          <p className="text-gray-300">
            <span className="font-semibold text-[#00ffc6]">Role:</span>{" "}
            {project.role}
          </p>
          <p className="text-gray-300">
            <span className="font-semibold text-[#00ffc6]">Tools:</span>{" "}
            {project.tools}
          </p>
        </div>

        {/* Content */}
        <div className="space-y-12">
          {/* Overview */}
          <section>
            <h2 className="text-2xl font-semibold text-[#00ffc6] mb-3">
              Overview
            </h2>
            <p className="text-gray-300 leading-relaxed">{project.overview}</p>
          </section>

          {/* Problem */}
          <section>
            <h2 className="text-2xl font-semibold text-[#00ffc6] mb-3">
              Problem
            </h2>
            <p className="text-gray-300 leading-relaxed">{project.problem}</p>
          </section>

          {/* Solution */}
          <section>
            <h2 className="text-2xl font-semibold text-[#00ffc6] mb-3">
              Solution
            </h2>
            <p className="text-gray-300 leading-relaxed">{project.solution}</p>
          </section>

          {/* Process (Bullet Points) */}
          <section>
            <h2 className="text-2xl font-semibold text-[#00ffc6] mb-3">
              Process
            </h2>
            <ul className="list-disc list-inside text-gray-300 space-y-2">
              {project.process
                .split(".")
                .filter((step) => step.trim() !== "")
                .map((step, idx) => (
                  <li key={idx}>{step.trim()}</li>
                ))}
            </ul>
          </section>

          {/* Outcome (Bullet Points) */}
          <section>
            <h2 className="text-2xl font-semibold text-[#00ffc6] mb-3">
              Outcome
            </h2>
            <ul className="list-disc list-inside text-gray-300 space-y-2">
              {project.outcome
                .split(".")
                .filter((item) => item.trim() !== "")
                .map((item, idx) => (
                  <li key={idx}>{item.trim()}</li>
                ))}
            </ul>
          </section>

          {/* Reflection */}
          <section className="border-t border-gray-800 pt-6">
            <h2 className="text-2xl font-semibold text-[#00ffc6] mb-3">
              Reflection
            </h2>
            <p className="text-gray-300 leading-relaxed">
              {project.reflection}
            </p>
          </section>
        </div>

        {/* Gallery */}
        {project.screens.length > 1 && (
          <div className="border-t border-gray-800 pt-8">
            <h3 className="text-xl font-semibold text-white mb-4">
              Screenshots
            </h3>
            <div className="grid grid-cols-1 sm:grid-cols-2 gap-6">
              {project.screens.slice(1).map((img, i) => (
                <img
                  key={i}
                  src={img}
                  alt={`${project.title} screenshot ${i + 2}`}
                  className="rounded-lg border border-gray-800 shadow-lg hover:scale-[1.02] transition-transform duration-300"
                />
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default ProjectDetail;
