import { useParams } from "react-router-dom";
import projects from "../data/projects";

function ProjectDetail() {
  const { slug } = useParams();
  const project = projects.find((p) => p.slug === slug);

  if (!project)
    return <div className="text-center text-white py-20">Project not found</div>;

  return (
    <div className="bg-[#0c0c0c] text-white px-6 py-16">
      <div className="max-w-6xl mx-auto space-y-12">
        {/* Back to Portfolio Button */}
        <div className="mb-6">
          <a
            href="/portfolio"
            className="inline-flex items-center gap-2 px-4 py-2 text-sm font-medium text-gray-200 bg-[#1a1a1a] rounded-full border border-gray-700 hover:bg-gray-800 hover:text-white transition duration-200"
          >
            ‚Üê Back to Portfolio
          </a>
        </div>

        {/* Title + Description */}
        <div className="text-center space-y-4">
          <h1 className="text-4xl md:text-5xl font-bold">{project.title}</h1>
          <p className="text-gray-400 max-w-2xl mx-auto">{project.description}</p>
        </div>

        {/* Hero Image */}
        {project.screens.length > 0 && (
          <div className="max-w-4xl mx-auto">
            <img
              src={project.screens[0]}
              alt={`${project.title} main screen`}
              className="w-full h-auto rounded-2xl border border-gray-800 shadow-lg"
            />
          </div>
        )}

        {/* Project Info */}
        <div className="flex flex-wrap gap-6 justify-center mt-6">
          <div className="bg-[#1a1a1a] px-5 py-3 rounded-xl border border-gray-700">
            <span className="text-gray-400 text-sm">Role</span>
            <p className="text-white font-medium">{project.role}</p>
          </div>
          <div className="bg-[#1a1a1a] px-5 py-3 rounded-xl border border-gray-700">
            <span className="text-gray-400 text-sm">Tools</span>
            <p className="text-white font-medium">{project.tools}</p>
          </div>
        </div>

        {/* Main Content */}
        <div className="space-y-12 mt-10">
          {/* Overview */}
          <section>
            <h2 className="text-2xl font-semibold mb-3">Overview</h2>
            <p className="text-gray-300 leading-relaxed">{project.overview}</p>
          </section>

          {/* Problem */}
          <section>
            <h2 className="text-2xl font-semibold mb-3">Problem</h2>
            <ul className="list-disc list-inside text-gray-400 space-y-2">
              {project.problem
                .split(". ")
                .filter((p) => p.trim())
                .map((point, index) => (
                  <li key={index}>{point.trim()}</li>
                ))}
            </ul>
          </section>

          {/* Solution */}
          <section>
            <h2 className="text-2xl font-semibold mb-3">Solution</h2>
            <ul className="list-disc list-inside text-gray-400 space-y-2">
              {project.solution
                .split(". ")
                .filter((p) => p.trim())
                .map((point, index) => (
                  <li key={index}>{point.trim()}</li>
                ))}
            </ul>
          </section>

          {/* Process */}
          <section>
            <h2 className="text-2xl font-semibold mb-3">Process</h2>
            <p className="text-gray-300 leading-relaxed">{project.process}</p>
          </section>

          {/* Outcome */}
          <section>
            <h2 className="text-2xl font-semibold mb-3">Outcome</h2>
            <p className="text-gray-300 leading-relaxed">{project.outcome}</p>
          </section>

          {/* Reflection */}
          <section>
            <h2 className="text-2xl font-semibold mb-3">Reflection</h2>
            <p className="text-gray-300 leading-relaxed">{project.reflection}</p>
          </section>
        </div>

        {/* Screenshots Grid */}
        {project.screens.length > 1 && (
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6 mt-14">
            {project.screens.slice(1).map((img, i) => (
              <img
                key={i}
                src={img}
                alt={`${project.title} screenshot ${i + 2}`}
                className="w-full rounded-xl border border-gray-800 shadow-lg hover:scale-105 transition-transform duration-300"
              />
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default ProjectDetail;
