import { useParams, Link } from "react-router-dom";
import projects from "../data/projects";

function ProjectDetails() {
  const { slug } = useParams();
  const project = projects.find((p) => p.slug === slug);

  if (!project) return (
    <div className="min-h-screen bg-black text-white flex items-center justify-center">
      <p className="text-lg">Project not found.</p>
    </div>
  );

  return (
    <div className="bg-[#0c0c0c] text-white">
      {/* Header Section */}
      <section className="px-6 py-24 max-w-5xl mx-auto">
        <h1 className="text-5xl md:text-6xl font-bold leading-tight">{project.title}</h1>
        <p className="mt-4 text-lg text-gray-400">{project.description}</p>
      </section>

      {/* Full-width hero image */}
      {project.screens?.[0] && (
        <div className="w-full">
          <img
            src={project.screens[0]}
            alt={`${project.title} Hero`}
            className="w-full h-auto object-cover"
          />
        </div>
      )}

      {/* Details Section */}
      <section className="px-6 py-24 max-w-5xl mx-auto space-y-20">
        {/* Role & Tools */}
        <div className="grid md:grid-cols-2 gap-12">
          <div>
            <h2 className="text-2xl font-semibold mb-3 text-gray-200">Role</h2>
            <p className="text-gray-400">{project.role}</p>
          </div>
          <div>
            <h2 className="text-2xl font-semibold mb-3 text-gray-200">Tools</h2>
            <p className="text-gray-400">{project.tools}</p>
          </div>
        </div>

        {/* Problem */}
        <div>
          <h2 className="text-3xl font-semibold mb-4">Problem</h2>
          <p className="text-gray-400 leading-relaxed text-lg">{project.problem}</p>
        </div>

        {/* Process */}
        <div>
          <h2 className="text-3xl font-semibold mb-4">Design Process</h2>
          <p className="text-gray-400 leading-relaxed text-lg">{project.process}</p>
        </div>

        {/* Screenshots Grid */}
        {project.screens?.length > 1 && (
          <div>
            <h2 className="text-3xl font-semibold mb-6">Gallery</h2>
            <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-6">
              {project.screens.slice(1).map((img, i) => (
                <img
                  key={i}
                  src={img}
                  alt={`Screenshot ${i + 2}`}
                  className="rounded-lg border border-gray-800 hover:scale-[1.01] transition-transform"
                />
              ))}
            </div>
          </div>
        )}

        {/* Outcome */}
        <div>
          <h2 className="text-3xl font-semibold mb-4">Outcome</h2>
          <p className="text-gray-400 leading-relaxed text-lg">{project.outcome}</p>
        </div>

        {/* Back Button */}
        <div className="pt-10">
          <Link
            to="/portfolio"
            className="inline-block text-white border border-white px-6 py-3 rounded hover:bg-white hover:text-black transition duration-300"
          >
            ‚Üê Back to Portfolio
          </Link>
        </div>
      </section>
    </div>
  );
}

export default ProjectDetails;
