import { useParams } from "react-router-dom";
import projects from "../data/projects";

function ProjectDetails() {
  const { slug } = useParams();
  const project = projects.find((p) => p.slug === slug);

  if (!project) return <p className="text-white text-center mt-20">Project not found</p>;

  return (
    <section className="bg-[#0c0c0c] text-white px-6 py-24">
      <div className="max-w-4xl mx-auto space-y-10">
        <h1 className="text-4xl font-bold">{project.title}</h1>
        <p className="text-gray-400">{project.description}</p>

        <div className="grid md:grid-cols-2 gap-4">
          <div>
            <p><strong>Role:</strong> {project.role}</p>
            <p><strong>Tools:</strong> {project.tools}</p>
            <p><strong>Problem:</strong> {project.problem}</p>
            <p><strong>Process:</strong> {project.process}</p>
            <p><strong>Outcome:</strong> {project.outcome}</p>
          </div>
          <div>
            {project.screens.map((img, i) => (
              <img key={i} src={img} alt="Project screenshot" className="rounded-lg border border-gray-800 mb-4" />
            ))}
          </div>
        </div>
      </div>
    </section>
  );
}

export default ProjectDetails;
